-- Insert 37 audio channels from old database to new database
-- Using UPSERT to handle duplicates and allow restart

ON CONFLICT (id) DO UPDATE SET
  channel_number = EXCLUDED.channel_number,
  channel_name = EXCLUDED.channel_name,
  description = EXCLUDED.description,
  brain_type_affinity = EXCLUDED.brain_type_affinity,
  neuroscience_tags = EXCLUDED.neuroscience_tags,
  image_url = EXCLUDED.image_url,
  display_order = EXCLUDED.display_order;